CREATE TABLE `unsolved_requests` (
`id_request` int(11)
,`subject_request` varchar(45)
,`details_request` text
,`picture_request` varchar(255)
,`it_staff_member_request` int(11)
,`date_time_request` datetime
,`expected_solution_date_request` date
,`created_by_request` varchar(45)
,`status_request` varchar(45)
);
DROP TABLE IF EXISTS `unsolved_requests`;

CREATE  VIEW `unsolved_requests` AS select `requests`.`id_request` AS `id_request`,`requests`.`subject_request` AS `subject_request`,`requests`.`details_request` AS `details_request`,`requests`.`picture_request` AS `picture_request`,`requests`.`it_staff_member_request` AS `it_staff_member_request`,`requests`.`date_time_request` AS `date_time_request`,`requests`.`expected_solution_date_request` AS `expected_solution_date_request`,`requests`.`created_by_request` AS `created_by_request`,`requests`.`status_request` AS `status_request` from `requests` where (`requests`.`status_request` <> 'Solved');


